  };

  render() {
    console.log(this.state.slots);
    return (
      <div className="App">
        <Header />

        <AddSlots />
        <div id="Parking">
          <Slots
            slots={this.state.slots}
            markBooked={this.markBooked}
            updateName={this.updateName}
            SlotsItem={this.SlotsItem}
          />
          <Slots
            slots={this.state.slots}
            markBooked={this.markBooked}
            updateName={this.updateName}
            SlotsItem={this.SlotsItem}
